import React from "react";
import { connect } from "unistore/react";
import { actions } from "../store";
import Media from "react-bootstrap/Media";
import Button from "react-bootstrap/Button";
import axios from "axios";
import { Link } from "react-router-dom";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBModalFooter,
  MDBIcon,
  MDBCardHeader,
  MDBModal,
  MDBModalBody,
  MDBBtn,
  MDBInput
} from "mdbreact";

class Transaksi extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      nama: "",
      detail: "",
      url: "",
      harga: 0,
      total: 0
    };
  }

  toggle = nr => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber]
    });
  };

  setNama = e => {
    e.preventDefault();
    this.setState({ nama: e.target.value });
  };

  setDetail = e => {
    e.preventDefault();
    this.setState({ detail: e.target.value });
  };

  setFotoItem = e => {
    e.preventDefault();
    this.setState({ url: e.target.value });
  };

  setHarga = e => {
    e.preventDefault();
    this.setState({ harga: e.target.value });
  };

  setTotal = e => {
    e.preventDefault();
    this.setState({ total: e.target.value });
  };

  changeHandle = e => {
    // this.props.setTotal(e.target.value);
    this.props.setJumlah(e.target.value);
    // this.setState({ question: e.target.value });
    // this.setState({ pic: "" });
  };

  componentDidMount() {
    const total = this.props.jum;
    const self = this;
    axios
      .get("http://127.0.0.1:5000/item/" + self.props.match.params.item_id)
      .then(function(response) {
        self.setState({ data: response.data });
        console.log(response.data);
      })
      .catch(function(error) {
        console.log("errrrrrr", error);
      });
  }

  render() {
    // if (this.props.is_login === false) {
    //   return <Redirect to={{ pathname: "/" }} />;
    // } else {
    return (
      <div>
        <div className="container pt-3">
          <div className="row">
            <div className="col-md-3 col-xs-2">
              <img
                width="100%"
                className="mr-3"
                src={this.state.data.url}
                alt="Generic placeholder"
              />
            </div>
            <div className="col-md-7 col-xs-12">
              <h2>{this.state.data.nama}</h2>
              <h5>{this.state.data.detail}</h5>
              <br />
              <h3 style={{ color: "red", fontWeight: 700 }}>
                Rp {this.state.data.harga}
              </h3>
              <br />
              <div className="row">
                <div className="col-md-4">Jumlah</div>
                <div className="col-md-4">{/* </input> */}</div>

                <div className="col-md-4">
                  <Link to={"/checkout/" + this.state.data.item_id}>
                    <MDBBtn
                      color="dark"
                      variant="success"
                      style={{ width: "120px", height: "50px" }}
                    >
                      Beli
                    </MDBBtn>
                  </Link>
                  <MDBBtn
                    color="dark"
                    variant="success"
                    style={{ width: "120px", height: "50px" }}
                    onClick={e => {
                      this.doDelete(e);
                    }}
                  >
                    Delete
                  </MDBBtn>

                  {/* <MDBContainer> */}
                  <MDBBtn
                    color="dark"
                    style={{ width: "120px", height: "50px" }}
                    onClick={this.toggle(14)}
                  >
                    Edit
                  </MDBBtn>
                </div>
              </div>
            </div>
          </div>
        </div>
        <br />
        <br />
      </div>
    );
  }
}
// }

export default connect(
  "is_login, jumlah",
  actions
)(Transaksi);
