import React, { Component } from "react";
import axios from "axios";

class Checkout extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      username: "",
      email: "",
      password: "",
      status: false
    };
  }

  doTransaki = async e => {
    e.preventDefault();
    // console.log("doregister", this.state);
    const self = this;
    axios
      .post("http://0.0.0.0:5000/user", {
        username: self.state.username,
        email: self.state.email,
        password: self.state.password,
        status: false
      })
      .then(function(response) {
        console.log(response.data);
        this.props.history.push("/");
      })
      .catch(function(error) {
        console.log("errrrrrr", error);
      });
    alert("Anda sudah terdaftar, yuk SignIn!");
  };

  render() {
    return (
      <div class="container">
        <table id="cart" class="table table-hover table-condensed">
          <thead>
            <tr>
              <th style={{ width: "50%" }}>Product</th>
              <th style={{ width: "10%" }}>Price</th>
              <th style={{ width: "8%" }}>Quantity</th>
              <th style={{ width: "22%" }} class="text-center">
                Subtotal
              </th>
              <th style={{ width: "10%" }} />
            </tr>
          </thead>
          <tbody>
            <tr>
              <td data-th="Product">
                <div class="row">
                  <div class="col-sm-2 hidden-xs">
                    <img
                      src="https://img.freepik.com/free-vector/money-bag-background-design_1270-41.jpg?size=338&ext=jpg"
                      style={{ width: "100%" }}
                      alt="..."
                      class="img-responsive"
                    />
                  </div>
                  <div class="col-sm-10">
                    <h4 class="nomargin">Product 1</h4>
                    <p>
                      Dengan donasi 50000 rupiah, sahabat mampu berbagi
                      kebahagian kepada orang-orang sekitar kita yang
                      membutuhkan.
                    </p>
                  </div>
                </div>
              </td>
              <td data-th="Price">5000</td>
              <td data-th="Quantity">
                <h5 class="text-center">1</h5>
              </td>
              <td data-th="Subtotal" class="text-center">
                5000
              </td>
              <td class="actions" data-th="">
                <button class="btn btn-info btn-sm">
                  <i class="fa fa-refresh" />
                </button>
                <button class="btn btn-danger btn-sm">
                  <i class="fa fa-trash-o" />
                </button>
              </td>
            </tr>
          </tbody>
          <tfoot>
            <tr class="visible-xs">
              <td class="text-center">
                <strong>5000</strong>
              </td>
            </tr>
            <tr>
              {/* <td>
              <a href="/" class="btn btn-warning">
                <i class="fa fa-angle-left" /> Kembali Memilih
              </a>
            </td> */}
              <td colspan="2" class="hidden-xs" />
              <td class="hidden-xs text-center">
                <strong>Total: Rp 5000</strong>
              </td>
              <td>
                <a href="/invoice" class="btn btn-success btn-block">
                  Proses Pembayaran <i class="fa fa-angle-right" />
                </a>
              </td>
            </tr>
          </tfoot>
        </table>
      </div>
    );
  }
}

export default Checkout;
