import React, { Component } from "react";
import "./media.css";
import axios from "axios";

class Invoice extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      username: "",
      email: "",
      password: "",
      status: false
    };
  }

  toggle = nr => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber]
    });
  };

  setUsername = e => {
    e.preventDefault();
    this.setState({ username: e.target.value });
  };

  setEmail = e => {
    e.preventDefault();
    this.setState({ email: e.target.value });
  };

  setPassword = e => {
    e.preventDefault();
    this.setState({ password: e.target.value });
  };

  setStatus = e => {
    e.preventDefault();
    this.setState({ status: e.target.value });
  };

  doRegister = async e => {
    e.preventDefault();
    // console.log("doregister", this.state);
    const self = this;
    axios
      .post("http://0.0.0.0:5000/user", {
        username: self.state.username,
        email: self.state.email,
        password: self.state.password,
        status: false
      })
      .then(function(response) {
        console.log(response.data);
        this.props.history.push("/");
      })
      .catch(function(error) {
        console.log("errrrrrr", error);
      });
    alert("Anda sudah terdaftar, yuk SignIn!");
  };

  render() {
    return (
      <div class="container">
        <div class="row justify-content-center">
          <div class="col-md-6 col-md-offset-3 main">
            <div class="col-md-12">
              <div class="row">
                <div class="col-md-4">
                  {/* <img
                  class="img-responsive"
                  alt="Invoce Template"
                  src="http://www.prepbootstrap.com/Content/images/template/productslider/product_004.jpg"
                /> */}
                </div>
                <div class="col-md-8 text-right">
                  <h2 style={{ color: "red" }}>
                    <strong>Berbagi</strong>
                  </h2>
                  <p>Jalan Tidar, Malang</p>
                  <p>0896 2772 5242</p>
                  <p>berbagi.com</p>
                </div>
              </div>
              <br />
              <div class="row">
                <div class="col-md-12 text-center">
                  <h2>INVOICE</h2>
                  <h5>00000846382</h5>
                </div>
              </div>
              <br />
              <div>
                <table class="table">
                  <thead>
                    <tr>
                      <th>
                        <h5>Description</h5>
                      </th>
                      <th>
                        <h5>Amount</h5>
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td class="col-md-9">Berbagi 50000</td>
                      <td class="col-md-3">
                        <i class="fa fa-usd" aria-hidden="true" /> 50000{" "}
                      </td>
                    </tr>
                    {/* <tr>
                    <td class="col-md-9">Apple iPhone 7 Plus 128 GB</td>
                    <td class="col-md-3">
                      <i class="fa fa-usd" aria-hidden="true" /> 1023,00{" "}
                    </td>
                  </tr> */}
                    {/* <tr>
                    <td class="col-md-9">Apple iPhone 6s Plus 16GB</td>
                    <td class="col-md-3">
                      <i class="fa fa-usd" aria-hidden="true" /> 595,00{" "}
                    </td>
                  </tr>
                  <tr>
                    <td class="col-md-9">Apple iPhone 6s Plus 64 GB</td>
                    <td class="col-md-3">
                      <i class="fa fa-usd" aria-hidden="true" /> 1052,00{" "}
                    </td>
                  </tr> */}
                    <tr>
                      <td class="text-right">
                        <p>
                          <strong>Total Amount: </strong>
                        </p>
                        <p>
                          <strong>Late Fees: </strong>
                        </p>
                        {/* <p>
                        <strong>Payable Amount: </strong>
                      </p>
                      <p>
                        <strong>Balance Due: </strong>
                      </p> */}
                      </td>
                      <td>
                        <p>
                          <strong>
                            <i class="fa fa-usd" aria-hidden="true" /> 50000{" "}
                          </strong>
                        </p>
                        <p>
                          <strong>
                            <i class="fa fa-usd" aria-hidden="true" /> 123
                          </strong>
                        </p>
                        {/* <p>
                        <strong>
                          <i class="fa fa-usd" aria-hidden="true" /> 159,00{" "}
                        </strong>
                      </p>
                      <p>
                        <strong>
                          <i class="fa fa-usd" aria-hidden="true" /> 5010,00{" "}
                        </strong>
                      </p> */}
                      </td>
                    </tr>
                    <tr style={{ color: "#F81D2D" }}>
                      <td class="text-right">
                        <h4>
                          <strong>Total:</strong>
                        </h4>
                      </td>
                      <td class="text-left">
                        <h4>
                          <strong>
                            <i class="fa fa-usd" aria-hidden="true" /> 50123{" "}
                          </strong>
                        </h4>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
              <div>
                <div class="col-md-12">
                  <p>
                    <b>Date :</b> 22 Agustus 2019
                  </p>
                  <br />
                  <h5>
                    <b>Informasi Pembayaran</b>
                  </h5>
                  <h6>Silahkan melakukan pembayaran via :</h6>
                  <div class="table-responsive text-nowrap">
                    <table class="table">
                      <tbody>
                        <tr>
                          <td>Nama Bank</td>
                          <td>Bank BNI (kode bank 009)</td>
                        </tr>
                        <tr>
                          <td>No Rekening</td>
                          <td>0395254909 a/n Ammar Al Faruqi</td>
                        </tr>
                        <tr>
                          <td>Jumlah Transfer</td>
                          <td>Rp 50.123</td>
                        </tr>
                        <tr>
                          <td>Batas Transfer</td>
                          <td>24 jam setelah invoice ini keluar</td>
                        </tr>
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Invoice;
