import React, { Component } from "react";
import {
  MDBContainer,
  MDBCarousel,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBTestimonial,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardUp,
  MDBAvatar,
  MDBCardBody,
  MDBIcon
} from "mdbreact";
import { connect } from "unistore/react";
import { actions } from "../../store";
import { Redirect } from "react-router-dom";
import axios from "axios";

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      username: "",
      password: "",
      data: []
    };
    this.doLogin = this.doLogin.bind(this);
  }

  setUsername = e => {
    e.preventDefault();
    this.setState({ username: e.target.value });
  };

  setPassword = e => {
    e.preventDefault();
    this.setState({ password: e.target.value });
  };

  componentDidMount() {
    const self = this;
    var config = {
      headers: {
        Authorization: "Bearer " + this.props.token
      }
    };
    axios
      .get("http://0.0.0.0:5000/user", config)
      .then(response => {
        console.log("get user", response.data);
        self.setState({ data: response.data });
      })
      .catch(error => {
        console.log("error user_details", error);
      });
  }

  render() {
    if (this.props.is_login == false) {
      return <Redirect to={{ pathname: "/" }} />;
    } else {
      return (
        <MDBContainer>
          <section className="text-center my-5">
            <h2 className="h1-responsive font-weight-bold my-5">Profile</h2>

            <div>
              <div className="mx-auto mb-4">
                <img
                  src="https://cdn3.iconfinder.com/data/icons/users-6/100/654853-user-men-2-512.png"
                  className="rounded-circle img-fluid"
                  style={{ height: "180px" }}
                />
              </div>
              <p>
                <MDBIcon icon="quote-left" /> Lorem ipsum dolor sit amet,
                consectetur adipisicing elit. Quod eos id officiis hic tenetur
                quae quaerat ad velit ab. Lorem ipsum dolor sit amet,
                consectetur adipisicing elit. Dolore cum accusamus eveniet
                molestias voluptatum inventore laboriosam labore sit, aspernatur
                praesentium iste impedit quidem dolor veniam.
              </p>
              <h3 className="font-weight-bold">Anna Deynah</h3>
              <h6 className="font-weight-bold my-3">annadeynah@gmail.com</h6>
            </div>
          </section>
        </MDBContainer>
      );
    }
  }
}

// const Profile = props => {

//     return (

//     );
// };

export default connect(
  "is_login, token",
  actions
)(Profile);
