import React from "react";
import { connect } from "unistore/react";
import { actions } from "../store";
import Media from "react-bootstrap/Media";
import Button from "react-bootstrap/Button";
import axios from "axios";
import { Link } from "react-router-dom";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBModalFooter,
  MDBIcon,
  MDBCardHeader,
  MDBModal,
  MDBModalBody,
  MDBBtn,
  MDBInput,
  MDBMedia
} from "mdbreact";

class Transaksi extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: []
    };
  }

  toggle = nr => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber]
    });
  };

  // setNama = e => {
  //   e.preventDefault();
  //   this.setState({ nama: e.target.value });
  // };

  // setDetail = e => {
  //   e.preventDefault();
  //   this.setState({ detail: e.target.value });
  // };

  // setFotoItem = e => {
  //   e.preventDefault();
  //   this.setState({ url: e.target.value });
  // };

  // setHarga = e => {
  //   e.preventDefault();
  //   this.setState({ harga: e.target.value });
  // };

  // setTotal = e => {
  //   e.preventDefault();
  //   this.setState({ total: e.target.value });
  // };

  changeHandle = e => {
    // this.props.setTotal(e.target.value);
    this.props.setJumlah(e.target.value);
    // this.setState({ question: e.target.value });
    // this.setState({ pic: "" });
  };

  componentDidMount() {
    const self = this;
    var config = {
      headers: {
        Authorization: "Bearer " + this.props.token
      }
    };
    axios
      .get("http://0.0.0.0:5000/transaksi", config)
      .then(function(response) {
        self.setState({ data: response.data });
        console.log(response.data);
      })
      .catch(function(error) {
        console.log("eror trans", error);
      });
  }

  render() {
    // if (this.props.is_login === false) {
    //   return <Redirect to={{ pathname: "/" }} />;
    // } else {
    return (
      <div>
        <br />

        <h2>History Transaksi</h2>
        <br />

        {this.state.data.map((data, index) => {
          if (index < 10) {
            return (
              <MDBMedia>
                <MDBMedia left className="mr-3" href="#">
                  <MDBMedia
                    object
                    src="https://image.flaticon.com/icons/svg/879/879890.svg"
                    style={{ width: "120px", height: "120px" }}
                    alt=""
                  />
                </MDBMedia>
                <MDBMedia body>
                  <MDBMedia heading>{index + 1}</MDBMedia>
                  <p> {data.nama_item}</p>
                  <p>Tanggal : {data.tanggal}</p>
                  <p> Total beli : {data.total_qty}</p>
                  <p> Total bayar : {data.total_harga}</p>
                </MDBMedia>
              </MDBMedia>
            );
          }
        })}
      </div>
    );
  }
}
// }

export default connect(
  "is_login, token",
  actions
)(Transaksi);
