import React, { Component } from "react";
import "./media.css";
import axios from "axios";
import { connect } from "unistore/react";
import { actions } from "../store";

class Invoice extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      item_id: "",
      total_qty: "",
      total_harga: ""
    };
  }

  setUsername = e => {
    e.preventDefault();
    this.setState({ username: e.target.value });
  };

  setEmail = e => {
    e.preventDefault();
    this.setState({ email: e.target.value });
  };

  setPassword = e => {
    e.preventDefault();
    this.setState({ password: e.target.value });
  };

  setStatus = e => {
    e.preventDefault();
    this.setState({ status: e.target.value });
  };

  componentDidMount() {
    const total = this.props.jum;
    const self = this;
    axios
      .get("http://127.0.0.1:5000/item/" + self.props.match.params.item_id)
      .then(function(response) {
        self.setState({ data: response.data });
        console.log(response.data);
      })
      .catch(function(error) {
        console.log("errrrrrr", error);
      });
  }

  doTransaki = async e => {
    e.preventDefault();
    // console.log("doregister", this.state);
    const self = this;
    axios
      .post("http://0.0.0.0:5000/transaki", {
        item_id: self.props.match.params.item_id,
        total_qty: self.props.jumlah,
        total_harga: self.props.jumlah * self.state.data.harga
      })
      .then(function(response) {
        console.log(response.data);
        this.props.history.push("/");
      })
      .catch(function(error) {
        console.log("errrrrrr", error);
      });
    alert("Anda sudah terdaftar, yuk SignIn!");
  };

  render() {
    return (
      <div class="container">
        <div className="row justify-content-center">
          <div class="col-md-6 col-md-offset-3 main">
            <div class="col-md-12">
              <div class="row">
                <div class="col-md-4">
                  {/* <img
                  class="img-responsive"
                  alt="Invoce Template"
                  src="http://www.prepbootstrap.com/Content/images/template/productslider/product_004.jpg"
                /> */}
                </div>
                <div class="col-md-8 text-right">
                  <h2 style={{ color: "red" }}>
                    <strong>Berbagi</strong>
                  </h2>
                  <p>Jalan Tidar, Malang</p>
                  <p>0896 2772 5242</p>
                  <p>berbagi.com</p>
                </div>
              </div>
              <br />
              <div class="row">
                <div class="col-md-12 text-center">
                  <h2>INVOICE</h2>
                  <h5>00000846382</h5>
                </div>
              </div>
              <br />
              <div>
                <table class="table">
                  <thead>
                    <tr>
                      <th>
                        <h5>Description</h5>
                      </th>
                      <th>
                        <h5>Quantity</h5>
                      </th>
                      <th>
                        <h5>Amount</h5>
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td class="col-md-8">{this.state.data.nama}</td>
                      <td class="col-md-4 text-center">
                        <i class="fa fa-usd " aria-hidden="true" />
                        {this.props.jumlah}
                      </td>
                      <td class="col-md-4 text-center">
                        <i class="fa fa-usd" aria-hidden="true" />
                        Rp {this.props.jumlah * this.state.data.harga}
                      </td>
                    </tr>
                    {/* <tr>
                    <td class="col-md-9">Apple iPhone 7 Plus 128 GB</td>
                    <td class="col-md-3">
                      <i class="fa fa-usd" aria-hidden="true" /> 1023,00{" "}
                    </td>
                  </tr> */}
                    {/* <tr>
                    <td class="col-md-9">Apple iPhone 6s Plus 16GB</td>
                    <td class="col-md-3">
                      <i class="fa fa-usd" aria-hidden="true" /> 595,00{" "}
                    </td>
                  </tr>
                  <tr>
                    <td class="col-md-9">Apple iPhone 6s Plus 64 GB</td>
                    <td class="col-md-3">
                      <i class="fa fa-usd" aria-hidden="true" /> 1052,00{" "}
                    </td>
                  </tr> */}
                    <tr>
                      <td class=" col-md-3 text-right" />
                      <td class=" col-md-3 text-right">
                        <p>
                          <strong>Total Amount: </strong>
                        </p>
                        <p>
                          <strong>Code Unique: </strong>
                        </p>
                        {/* <p>
                        <strong>Payable Amount: </strong>
                      </p>
                      <p>
                        <strong>Balance Due: </strong>
                      </p> */}
                      </td>
                      <td class=" col-md-3 text-center">
                        <p>
                          <strong />
                        </p>
                        <p>
                          <strong>
                            <i class="fa fa-usd" aria-hidden="true" />
                            {this.props.jumlah * this.state.data.harga}
                          </strong>
                        </p>
                        <p>
                          <strong />
                        </p>
                        <p>
                          <strong>
                            <i class="fa fa-usd" aria-hidden="true" /> 123
                          </strong>
                        </p>
                        {/* <p>
                        <strong>
