import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import ButtonBase from "@material-ui/core/ButtonBase";
import Container from "@material-ui/core/Container";
import Typography from "../../component/typo";
import "./home1.css";
import { Link } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../../store";
import { Redirect } from "react-router-dom";
import Carousel from "react-bootstrap/Carousel";

const styles = theme => ({
  root: {
    marginTop: theme.spacing(8),
    marginBottom: theme.spacing(4)
  },
  images: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexWrap: "wrap"
  },
  imageWrapper: {
    position: "relative",
    display: "block",
    padding: 0,
    borderRadius: 0,
    height: "40vh",
    [theme.breakpoints.down("sm")]: {
      width: "100% !important",
      height: 100
    },
    "&:hover": {
      zIndex: 1
    },
    "&:hover $imageBackdrop": {
      opacity: 0.15
    },
    "&:hover $imageMarked": {
      opacity: 0
    },
    "&:hover $imageTitle": {
      border: "4px solid currentColor"
    }
  },
  imageButton: {
    position: "absolute",
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    color: theme.palette.common.white
  },
  imageSrc: {
    position: "absolute",
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    backgroundSize: "cover",
    backgroundPosition: "center 40%"
  },
  imageBackdrop: {
    position: "absolute",
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    background: theme.palette.common.black,
    opacity: 0.5,
    transition: theme.transitions.create("opacity")
  },
  imageTitle: {
    position: "relative",
    padding: `${theme.spacing(2)}px ${theme.spacing(4)}px 14px`
  },
  imageMarked: {
    height: 3,
    width: 18,
    background: theme.palette.common.white,
    position: "absolute",
    bottom: -2,
    left: "calc(50% - 9px)",
    transition: theme.transitions.create("opacity")
  }
});

function Home1(props) {
  const { classes } = props;

  const images = [
    {
      url:
        "https://img.freepik.com/free-vector/money-bag-background-design_1270-41.jpg?size=338&ext=jpg",
      title: "Berbagi 5.000",
      width: "33%",
      link: "/donasi5000"
    },
    {
      url:
        "https://img.freepik.com/free-vector/money-bag_16734-108.jpg?size=338&ext=jpg",
      title: "10.000",
      width: "33%",
      link: "/donasi10000"
    },
    {
      url:
        "https://www.akun.biz/tips-bisnis/wp-content/uploads/2018/05/money-bags-3404346_960_720.jpg",
      title: "50.000",
      width: "33%",
      link: "/donasi50000"
    }
  ];

  return (
    <div className="animated infinited bounce">
      <Container className={classes.root} component="section">
        <Typography
          // className="garis"
          variant="h4"
          marked="center"
          align="center"
          component="h2"
        >
          <div className="animated jackInTheBox">
            Pilih Nominal Yang Ingin Anda Bagikan
          </div>
        </Typography>
        <div className={classes.images}>
          {images.map(image => (
            <ButtonBase
              key={image.title}
              className={classes.imageWrapper}
              style={{
                width: image.width
              }}
              onClick={() => props.history.push(image.link)}
            >
              <div
                className={classes.imageSrc}
                style={{
                  backgroundImage: `url(${image.url})`
                }}
              />
              <div className={classes.imageBackdrop} />
              <div className={classes.imageButton}>
                <Typography
                  component="h3"
                  variant="h6"
                  color="inherit"
                  className={classes.imageTitle}
                >
                  {image.title}
                  <div className={classes.imageMarked} />
                </Typography>
                <Typography
                  component="h3"
                  variant="h6"
                  color="inherit"
                  className={classes.imageTitle}
                >
                  Total pendonasi: 5
                  <div className={classes.imageMarked} />
                </Typography>
              </div>
            </ButtonBase>
          ))}
        </div>
        <div />
      </Container>

      <br />
      <br />
      <br />
      <br />
      <div className="text-center jackInTheBox">
        Kegiatan-kegiatan yang telah dilakukan
      </div>
      <br />

      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-6">
            <Carousel>
              <Carousel.Item>
                <img
                  className="d-block w-100"
                  src="https://kitabisa-userupload-01.s3-ap-southeast-1.amazonaws.com/_production/campaign/72057/31_72057_1533723292_851704_f.png"
                  alt="First slide"
                />
                {/* <Carousel.Caption>
                  <h3>First slide label</h3>
                  <p>
                    Nulla vitae elit libero, a pharetra augue mollis interdum.
                  </p>
                </Carousel.Caption> */}
              </Carousel.Item>
              <Carousel.Item>
                <img
                  className="d-block w-100"
                  src="https://www.hidayatullah.com/files/bfi_thumb/RamadhanBagiIfhtar-2xoca7tahvmpuftrnkpt6o.jpg"
                  alt="Third slide"
                />

                {/* <Carousel.Caption>
                  <h3>Second slide label</h3>
                  <p>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                  </p>
                </Carousel.Caption> */}
              </Carousel.Item>
              <Carousel.Item>
                <img
                  className="d-block w-100"
                  src="https://sdk11.bpkpenaburjakarta.or.id/wp-content/uploads/sites/32/2013/12/IMG_4975.jpg"
                  alt="Third slide"
                />

                {/* <Carousel.Caption>
                  <h3>Third slide label</h3>
                  <p>
                    Praesent commodo cursus magna, vel scelerisque nisl
                    consectetur.
                  </p>
                </Carousel.Caption> */}
              </Carousel.Item>
            </Carousel>
          </div>
        </div>
      </div>
    </div>
  );
}

Home1.propTypes = {
  classes: PropTypes.object.isRequired
};

export default connect(
  "is_login",
  actions
)(withStyles(styles)(Home1));
