import React from "react";
import {
  MDBContainer,
  MDBCarousel,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBTestimonial,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardUp,
  MDBAvatar,
  MDBCardBody,
  MDBIcon
} from "mdbreact";
import { connect } from "unistore/react";
import { actions } from "../../store";
import { Redirect } from "react-router-dom";
import axios from "axios";

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      username: "",
      password: ""
    };
    this.doLogin = this.doLogin.bind(this);
  }

  setUsername = e => {
    e.preventDefault();
    this.setState({ username: e.target.value });
  };

  setPassword = e => {
    e.preventDefault();
    this.setState({ password: e.target.value });
  };

  setToken = e => {
    e.preventDefault();
    this.setState({ token: e.target.value });
  };

  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  };

  doLogin = async e => {
    // e.preventDefault();
    const self = this;
    axios
      .post("http://0.0.0.0:5000/auth", {
        username: self.state.username,
        password: self.state.password
      })
      .then(function(response) {
        console.log(response.data);
        self.props.setLogin(true);
        // self.setToken(response.data.token);
        self.props.setToken(response.data.token);
        self.props.history.replace("/profile");
        console.log("props login", self.props.is_login);
      })
      .catch(function(error) {
        console.log("errrrrrr", error);
      });
    alert("Selamat Datang Orang Baik!");
  };

  render() {
    if (props.is_login == false) {
      return <Redirect to={{ pathname: "/" }} />;
    } else {
      return (
        <MDBContainer>
          <section className="text-center my-5">
            <h2 className="h1-responsive font-weight-bold my-5">Profile</h2>

            <div>
              <div className="mx-auto mb-4">
                <img
                  src="https://cdn3.iconfinder.com/data/icons/users-6/100/654853-user-men-2-512.png"
                  className="rounded-circle img-fluid"
                  style={{ height: "180px" }}
                />
              </div>
              <p>
                <MDBIcon icon="quote-left" /> Lorem ipsum dolor sit amet,
                consectetur adipisicing elit. Quod eos id officiis hic tenetur
                quae quaerat ad velit ab. Lorem ipsum dolor sit amet,
                consectetur adipisicing elit. Dolore cum accusamus eveniet
                molestias voluptatum inventore laboriosam labore sit, aspernatur
                praesentium iste impedit quidem dolor veniam.
              </p>
              <h3 className="font-weight-bold">Anna Deynah</h3>
              <h6 className="font-weight-bold my-3">annadeynah@gmail.com</h6>
            </div>
          </section>
        </MDBContainer>
      );
    }
  }
}

// const Profile = props => {

//     return (

//     );
// };

export default connect(
  "is_login",
  actions
)(Profile);
