import React from "react";
import { connect } from "unistore/react";
import { actions } from "../../store";
import Media from "react-bootstrap/Media";
import Button from "react-bootstrap/Button";
import Jumlah from "../../component/jumlah";
import Total from "../../component/totalharga";
import axios from "axios";

class Detail extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: []
    };
  }

  changeHandle = e => {
    // this.props.setTotal(e.target.value);
    this.props.setJumlah(e.target.value);
    // this.setState({ question: e.target.value });
    // this.setState({ pic: "" });
  };

  componentDidMount() {
    const self = this;
    axios
      .get("http://127.0.0.1:5000/item")
      .then(function(response) {
        self.setState({ data: response.data });
        console.log(response.data);
      })
      .catch(function(error) {
        console.log("errrrrrr", error);
      });
  }
  render() {
    // if (this.props.is_login === false) {
    //   return <Redirect to={{ pathname: "/" }} />;
    // } else {
    return (
      <div>
        {this.state.data.map((data, index) => {
          if (index < 5) {
            return (
              <div className="container">
                <div className="row">
                  <div className="col-md-3 col-xs-2">
                    <img
                      width="100%"
                      className="mr-3"
                      src={data.url}
                      alt="Generic placeholder"
                    />
                  </div>
                  <div className="col-md-7 col-xs-12">
                    <h2>{data.nama}</h2>
                    <h5>{data.detail}</h5>
                    <br />
                    <h3 style={{ color: "red", fontWeight: 700 }}>
                      Rp {data.harga}
                    </h3>
                    <br />
                    <div className="row">
                      <div className="col-md-4">
                        Jumlah
                        <Jumlah onChange={this.changeHandle} />
                      </div>
                      <div className="col-md-4">
                        Total Harga
                        <h4 className="text-black">
                          Rp {this.props.jumlah * 50}
                        </h4>
                      </div>
                      <div className="col-md-4">
                        <a href="/checkout">
                          <button
                            variant="success"
                            style={{ width: "100px", height: "50px" }}
                          >
                            Beli
                          </button>
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              // <MDBCol lg="3" md="6" sm="6" className="mb-lg-0 mb-4 pb-4">
              //   <a href="/donasi50000">
              //     <MDBCard collection className="z-depth-1-half">
              //       <div className="view zoom">
              //         <img
              //           src={data.url}
              //           className="img-fluid"
              //           style={{ height: "300px" }}
              //         />
              //         <div className="stripe dark">
              //           <p>
              //             {data.nama} <MDBIcon icon="angle-right" />
              //           </p>
              //           <p>Total pendonasi: {data.total}</p>
              //         </div>
              //       </div>
              //     </MDBCard>
              //   </a>
              // </MDBCol>
            );
          }
        })}
        <br />
        <br />
      </div>
    );
  }
}
// }

export default connect(
  "is_login, jumlah",
  actions
)(Detail);
