import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBRow,
  MDBCol,
  MDBInput
} from "mdbreact";
import axios from "axios";
import { connect } from "unistore/react";
import { actions } from "../store";
import { withRouter, Link, Redirect } from "react-router-dom";

class Delete extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      item_id: ""
    };
    this.doDelete = this.doDelete.bind(this);
  }

  setItem_id = e => {
    e.preventDefault();
    this.setState({ item_id: e.target.value });
  };

  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  };

  doDelete = async e => {
    // e.preventDefault();
    const self = this;
    axios
      .delete("http://127.0.0.1:5000/item/" + String(self.state.item_id), {
        item_id: self.state.item_id
      })
      .then(function(response) {
        console.log(response.data);
        // self.props.setLogin(true);
        // self.setToken(response.data.token);
        // self.props.setToken(response.data.token);
        // console.log(response.data.status);
        // if (response.data.status == true) {
        //   self.props.history.replace("/profile");
        // } else {
        //   self.props.history.replace("/home");
        // }
        console.log("props login", self.props.is_login);
      })
      .catch(function(error) {
        console.log("eror delete", error);
      });
    alert("Selamat Datang Orang Baik!");
  };

  render() {
    return (
      <MDBContainer>
        <MDBBtn color="primary" onClick={this.toggle}>
          Delete
        </MDBBtn>
        <MDBModal isOpen={this.state.modal} toggle={this.toggle}>
          {/* <MDBModalHeader toggle={this.toggle}>Login</MDBModalHeader> */}
          <MDBModalBody>
            <MDBContainer>
              <MDBRow>
                <MDBCol md="10">
                  <form>
                    <p className="h5 text-center mb-4">Sign in</p>
                    <div className="grey-text">
                      <MDBInput
                        label="Item_id yang ingin dihapus"
                        // icon="envelope"
                        group
                        type="username"
                        validate
                        error="wrong"
                        success="right"
                        onChange={this.setItem_id}
                      />
                    </div>
                  </form>
                </MDBCol>
              </MDBRow>
            </MDBContainer>
          </MDBModalBody>
          <MDBModalFooter>
            {/* <Link to="/profile"> */}
            <button color="primary" onClick={this.doDelete}>
              Delete
            </button>
            {/* </Link> */}
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default connect(
  "is_login",
  actions
)(withRouter(Delete));
