import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBRow,
  MDBCol,
  MDBInput
} from "mdbreact";
import axios from "axios";
import { connect } from "unistore/react";
import { actions } from "../../store";
import { withRouter, Link, Redirect } from "react-router-dom";

class SignIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      username: "",
      password: "",
      redirect: false
    };
    this.doLogin = this.doLogin.bind(this);
  }

  setRedirect = () => {
    this.setState({
      redirect: true
    });
  };

  setUsername = e => {
    e.preventDefault();
    this.setState({ username: e.target.value });
  };

  setPassword = e => {
    e.preventDefault();
    this.setState({ password: e.target.value });
  };

  setToken = e => {
    e.preventDefault();
    this.setState({ token: e.target.value });
  };

  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  };

  doLogin = async e => {
    // e.preventDefault();
    const self = this;
    axios
      .post("http://0.0.0.0:5000/auth", {
        username: self.state.username,
        password: self.state.password
      })
      .then(function(response) {
        console.log(response.data);
        self.props.setLogin(false);
        self.setToken(response.data.token);
        localStorage.setItem("token", response.data.token);
        console.log("props login", this.props.is_login);
        self.props.history.replace("/profile");
        if (this.state.redirect) {
          return <Redirect to="/profile" />;
        }
      })
      .catch(function(error) {
        console.log("errrrrrr", error);
      });
  };

  render() {
    return (
      <MDBContainer>
        <MDBBtn color="primary" onClick={this.toggle}>
          Sign In
        </MDBBtn>
        <MDBModal isOpen={this.state.modal} toggle={this.toggle}>
          {/* <MDBModalHeader toggle={this.toggle}>Login</MDBModalHeader> */}
          <MDBModalBody>
            <MDBContainer>
              <MDBRow>
                <MDBCol md="10">
                  <form>
                    <p className="h5 text-center mb-4">Sign in</p>
                    <div className="grey-text">
                      <MDBInput
                        label="Type your username"
                        // icon="envelope"
                        group
                        type="username"
                        validate
                        error="wrong"
                        success="right"
                        onChange={this.setUsername}
                      />
                      <MDBInput
                        label="Type your password"
                        // icon="lock"
                        group
                        type="password"
                        validate
                        onChange={this.setPassword}
                      />
                    </div>
                  </form>
                </MDBCol>
              </MDBRow>
            </MDBContainer>
          </MDBModalBody>
          <MDBModalFooter>
            {/* <Link to="/profile"> */}
            <button color="primary" onClick={this.doLogin}>
              Login
            </button>
            {/* </Link> */}
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default connect(
  "is_login",
  actions
)(withRouter(SignIn));
