import React from "react";
import axios from "axios";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardImage,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardFooter,
  MDBIcon,
  MDBTooltip,
  MDBBadge,
  MDBCarousel,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBBtn
} from "mdbreact";

class Donasi extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: []
    };
  }

  componentDidMount() {
    const self = this;
    axios
      .get("http://127.0.0.1:5000/item")
      .then(function(response) {
        self.setState({ data: response.data });
        console.log(response.data);
      })
      .catch(function(error) {
        console.log("errrrrrr", error);
      });
  }

  render() {
    return (
      <section className="text-center my-5">
        <h2 className="h1-responsive font-weight-bold text-center my-5">
          Our bestsellers
        </h2>
        <p className="grey-text text-center w-responsive mx-auto mb-5">
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Fugit, error
          amet numquam iure provident voluptate esse quasi, veritatis totam
          voluptas nostrum quisquam eum porro a pariatur veniam.
        </p>
        <MDBRow>
          {this.state.data.map((data, index) => {
            if (index < 5) {
              return (
                <MDBCol lg="3" md="6" className="mb-lg-0 mb-4">
                  <MDBCard collection className="z-depth-1-half">
                    <div className="view zoom">
                      <img
                        src="https://mdbootstrap.com/img/Photos/Horizontal/E-commerce/Vertical/5.jpg"
                        className="img-fluid"
                        alt=""
                      />
                      <div className="stripe dark">
                        <a href="#!">
                          <p>
                            Red trousers <MDBIcon icon="angle-right" />
                          </p>
                        </a>
                      </div>
                    </div>
                  </MDBCard>
                </MDBCol>
                // <li className="list-group-item d-flex colWidth justify-content-between align-items-center">
                //   <img src={data.url} />
                //   {data.nama}
                //   {data.harga}
                //   {data.detail}
                //   <br />
                //   <span className="badge badge-primary warnaCol badge-pill">
                //     #{index + 1}
                //   </span>
                // </li>
              );
            }
          })}
          <MDBCol lg="3" md="6" className="mb-lg-0 mb-4">
            <MDBCard collection className="z-depth-1-half">
              <div className="view zoom">
                <img
                  src="https://mdbootstrap.com/img/Photos/Horizontal/E-commerce/Vertical/5.jpg"
                  className="img-fluid"
                  alt=""
                />
                <div className="stripe dark">
                  <a href="#!">
                    <p>
                      Red trousers <MDBIcon icon="angle-right" />
                    </p>
                  </a>
                </div>
              </div>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </section>
    );
  }
}

export default Donasi;
