import React from "react";
import { connect } from "unistore/react";
import { actions } from "../../store";
import Media from "react-bootstrap/Media";
import Button from "react-bootstrap/Button";
import Jumlah from "../../component/jumlah";
import Total from "../../component/totalharga";

class Donasi5000 extends React.Component {
  changeHandle = e => {
    // this.props.setTotal(e.target.value);
    this.props.setJumlah(e.target.value);
    // this.setState({ question: e.target.value });
    // this.setState({ pic: "" });
  };

  render() {
    // if (this.props.is_login === false) {
    //   return <Redirect to={{ pathname: "/" }} />;
    // } else {
    return (
      <div>
        <br />
        <br />
        <div className="container">
          <div className="row">
            <div className="col-md-3 col-xs-6">
              <img
                width="100%"
                className="mr-3"
                src="https://img.freepik.com/free-vector/money-bag-background-design_1270-41.jpg?size=338&ext=jpg"
                alt="Generic placeholder"
              />
            </div>
            <div className="col-md-7 col-xs-12">
              <h2>Donasi 5000</h2>
              <h5>
                Dengan donasi 5000 rupiah, sahabat mampu berbagi kebahagian
                kepada orang-orang sekitar kita yang membutuhkan.
              </h5>
              <br />
              <h3 style={{ color: "red", fontWeight: 700 }}>Rp 5.000</h3>
              <br />
              <div className="row">
                <div className="col-md-4">
                  Jumlah
                  <Jumlah onChange={this.changeHandle} />
                </div>
                <div className="col-md-4">
                  Total Harga
                  {/* <Total>
                    <h4 className="text-black">
                      Rp {this.props.jumlah * 5}.000
                    </h4>
                  </Total> */}
                  <h4 className="text-black">Rp {this.props.jumlah * 5}.000</h4>
                </div>
                <div className="col-md-4">
                  <a href="/checkout">
                    <Button variant="success" style={{ width: "100px" }}>
                      Beli
                    </Button>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
// }

export default connect(
  "is_login, jumlah",
  actions
)(Donasi5000);
